calculate.script:
if (UPI.rawValue == null) {
   this.rawValue = PenFormNumber.rawValue + "000000000"; 
}
else {
   if (UPI.rawValue < 0){
      UPI.rawValue = null;
      this.rawValue = PenFormNumber.rawValue + "000000000";
   }
   else{
      this.rawValue = PenFormNumber.rawValue + fRight.Right(("000000000" + UPI.rawValue),9);
   }
}

if (UPI.rawValue == null) {
   this.rawValue = ""; 
}
else {
   this.rawValue = UPI.rawValue; 
}

event.script:
xfa.host.setFocus(UPI);
this.presence = "hidden";
if (UPIgrp.FromPS.rawValue = 'N')
   this.Form751.UPIgrp.FromPS.rawValue = 'J';
else
   if (UPIgrp.FromPS.rawValue = 'S')
      this.Form751.UPIgrp.FromPS.rawValue = 'K';

if (UPIgrp.FromPS.rawValue == "Y"){
   this.presence = "invisible";
   xfa.host.setFocus(PrintButton);
}
if (UPIgrp.FromPS.rawValue == "J"){
   this.presence = "invisible";
   xfa.host.setFocus(UPIgrp.UPI);
}
if (UPIgrp.FromPS.rawValue == "K"){
   this.presence = "invisible";
   xfa.host.setFocus(OptionGrp.Option);
}

variables.script:
function Right(str, n){
    if (n <= 0)
       return "";
    else if (n > String(str).length)
       return str;
    else {
       var iLen = String(str).length;
       return String(str).substring(iLen, iLen - n);
    }
}

event.script:
var v_upi = topmostSubform.Form751.UPIgrp.UPI.rawValue;

if (v_upi == null || v_upi == "0"){
   xfa.event.cancelAction = 1;
   if (topmostSubform.Form751.Disclaimer.presence == "visible"){
      xfa.host.setFocus(topmostSubform.Form751.Disclaimer);
      xfa.host.messageBox("Please click yellow area to start.", "Form 751", 3);
   }
   else{      
      xfa.host.setFocus(topmostSubform.Form751.UPIgrp.UPI);
      xfa.host.messageBox("Please enter your UPI", "Form 751", 3);
   }
}

var sURL = event.target.URL;
var nRequestStart = sURL.indexOf("?");

// xfa.host.messageBox(this.Form751.UPIgrp.FromPS.rawValue);

if (nRequestStart > 0){
   var sRequest = sURL.substr(nRequestStart + 1);
   var aRequests = sRequest.split("&");

   for (var i = 0; i < aRequests.length; i++){
      var aNameValue = aRequests[i].split("=");
      var sName = aNameValue[0];
      var sValue = "";

      try{
         sValue = decodeURI(decodeURIComponent(aNameValue[1]));
      }
      catch (e){
         continue;    // invalid URL encoding found -- skip
      }
      if (sName.toUpperCase() == "FROMPS")
         this.Form751.UPIgrp.FromPS.rawValue = sValue.toUpperCase();
      if (sName.toUpperCase() == "UPI")
         this.Form751.UPIgrp.UPI.rawValue = sValue;
      if (sName.toUpperCase() == "NAME")
         this.Form751.Sec_1.Name.rawValue = unescape(sValue);
   }
}

// xfa.host.messageBox(this.Form751.UPIgrp.FromPS.rawValue);

if (this.Form751.UPIgrp.FromPS.rawValue == 'N')
   this.Form751.UPIgrp.FromPS.rawValue = 'J';
else
   if (this.Form751.UPIgrp.FromPS.rawValue == 'S')
      this.Form751.UPIgrp.FromPS.rawValue = 'K';

if (this.Form751.UPIgrp.FromPS.rawValue == "Y" || this.Form751.UPIgrp.FromPS.rawValue == "K"){
   var v_upi  = this.Form751.UPIgrp.UPI.rawValue;
   var v_name = this.Form751.Sec_1.Name.rawValue;
   if (v_upi != null && v_upi != "0")
      this.Form751.UPIgrp.UPI.access = "readOnly";
   if (v_name != null && v_name != " ")
      this.Form751.Sec_1.Name.access = "readOnly";
   this.Form751.Title.ResetButton.presence = "hidden";
}
